import React, { useState } from 'react';

const App = () => {
  // Récupérer l'historique depuis le stockage local
  const storedHistory = localStorage.getItem('messageHistory');
  const initialHistory = storedHistory ? JSON.parse(storedHistory) : [];

  // Utiliser useState pour gérer l'état de l'entrée et de l'historique
  const [inputValue, setInputValue] = useState('');
  const [history, setHistory] = useState(initialHistory);

  // Fonction pour gérer l'envoi du message
  const handleMessageSend = () => {
    if (inputValue.trim() !== '') {
      const newHistory = [...history, inputValue];
      setHistory(newHistory);
      localStorage.setItem('messageHistory', JSON.stringify(newHistory));
      setInputValue('');
    }
  };
  const handleDeleteMessage = (index) => {
    const updatedHistory = [...history];
    updatedHistory.splice(index, 1);
    setHistory(updatedHistory);
    localStorage.setItem('messageHistory', JSON.stringify(updatedHistory));
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', backgroundColor: '#3498db' }}>
      <div style={{ backgroundColor: '#ffffff', padding: '20px', borderRadius: '10px', boxShadow: '0 0 10px rgba(0, 0, 0, 0.2)' }}>
        {/* Champ de saisie et bouton d'envoi */}
        <input 
          type="text" 
          value={inputValue} 
          onChange={e => setInputValue(e.target.value)} 
          placeholder="Entrez votre message" 
          style={{ height: '40px', borderColor: 'gray', borderWidth: '1px', marginBottom: '10px', paddingHorizontal: '10px' }}
        />
        <button onClick={handleMessageSend}>Envoyer</button>

        {/* Affichage de l'historique */}
        <div>
          <h2>Historique des messages :</h2>
          <ul>
            {history.map((message, index) => (
              <li key={index}>{message}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default App;
