import React, { useState, useEffect } from 'react';

const App = () => {
  // State pour la valeur de l'entrée et l'historique des valeurs
  const [inputValue, setInputValue] = useState('');
  const [history, setHistory] = useState<string>([]);

  // Fonction pour gérer l'envoi du message
  const handleMessageSend = () => {
    if (inputValue.trim() !== '') {
      // Ajouter la nouvelle valeur à l'historique
      const newHistory = [...history, inputValue];
      setHistory(newHistory);
      // Mettre à jour le stockage local
      localStorage.setItem('messageHistory', JSON.stringify(newHistory));
      // Effacer la valeur de l'entrée
      setInputValue('');
    }
  };

  // Effet pour charger l'historique depuis le stockage local lors du montage du composant
  useEffect(() => {
    const storedHistory = localStorage.getItem('messageHistory');
    if (storedHistory) {
      setHistory(JSON.parse(storedHistory));
    }
  }, []);

  return (
    <div>
      {/* Champ de saisie et bouton d'envoi */}
      <input 
        type="text" 
        value={inputValue} 
        onChange={e => setInputValue(e.target.value)} 
        placeholder="Entrez votre message" 
      />
      <button onClick={handleMessageSend}>Envoyer</button>

      {/* Affichage de l'historique */}
      <div>
        <h2>Historique des messages :</h2>
        <ul>
          {history.map((message, index) => (
            <li key={index}>{message}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default App;
